float calculation3(struct marks mg1[20],int ch,int ch2){
     
     float totalsgp=0,point;
     int i,j;
 
for(j=0;j<ch+ch2;j++){  
total=total+mg1[j].credit;
}

for(i=0;i<ch+ch2;i++){     
totalsgp=totalsgp+((mg1[i].gpa/4)*(mg1[i].credit));
}
point=(totalsgp/total)*4;

return point;
}

float cgpa(float pgp,float point){
      
if(pgp==0){
           
return point;
}

else{
CGPA=((pgp+point)/8)*4;
return CGPA; 
}      
}       
